        /// <summary>
        /// Control representing content of 'About' dialog
        /// </summary>
        /// <param name="context">Wrapper of all program resources</param>
        public ControlAbout(Context context)
        {
            this.Context = context;
            InitializeComponent();
            this.textBoxLicense.Text = ControlAbout.License;
            this.textBoxThirdParty.Text = ControlAbout.ThirdParty;
            this.Load += ControlAbout_Load;
        }

        /// <summary>
        /// Handles load of control event
        /// </summary>
        /// <param name="sender">Sender of event</param>
        /// <param name="e">Arguments of event</param>
        private void ControlAbout_Load(object? sender, EventArgs e)
        {
            this.ResizeTextBox(this.textBoxLicense);
            this.ResizeTextBox(this.textBoxThirdParty);
        }

        /// <summary>
        /// Resizes textbox to fit content vertically
        /// </summary>
        /// <param name="textBox">Textbox which will be resized</param>
        private void ResizeTextBox(TextBox textBox)
        {
            const int padding = 3;
            int numLines = textBox.GetLineFromCharIndex(textBox.TextLength) + 1;
            int border = textBox.Height - textBox.ClientSize.Height;
            textBox.Height = textBox.Font.Height * numLines + padding + border;
        }

